    private void compileReport(String jrxmlPath, String jasperPath) throws JRException, IOException {
        InputStream jrxmlInputStream = getClass().getResourceAsStream(jrxmlPath);
        if (jrxmlInputStream == null) {
            throw new FileNotFoundException("Resource not found: " + jrxmlPath);
        }
        JasperDesign jasperDesign = JRXmlLoader.load(jrxmlInputStream);
        JasperReport jasperReport = JasperCompileManager.compileReport(jasperDesign);

        // Save the compiled Jasper file
        try (ObjectOutputStream oos = new ObjectOutputStream(new FileOutputStream(jasperPath))) {
            oos.writeObject(jasperReport);
        }
    }

    private String getCompiledReportPath(String resourceName) throws IOException {
        URL resourceUrl = getClass().getResource( resourceName);
        if (resourceUrl == null) {
            File compiledFile = new File("src/main/resources/" + resourceName);
            if (!compiledFile.exists()) {
                compiledFile.createNewFile();
            }
            return compiledFile.getAbsolutePath();
        }
        return new File(resourceUrl.getFile()).getAbsolutePath();
    }

